import { registerUser } from "../services/internalApiService"
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'

const Register = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState({
        usernameError: "",
        passwordError: ""
    })
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        const newUser = {
            username: username,
            password: password
        }
        let isValid = validateUser(newUser)
        if(isValid){
            registerUser(newUser)
            .then(data => {
                    console.log(data)
                    localStorage.setItem('token', data.user)
                    navigate('/login')
            })
            .catch(err => {
                const errorResponse = err.response.data.errors; 
                const errorArr = []; 
                for (const key of Object.keys(errorResponse)) { 
                    errorArr.push(errorResponse[key].message)
                }
                setErrors(errorArr);
            })
        }

    }

    const validateUser = (newUser) => {
        if(newUser.username.length < 5) {
            setStyle({
                ...style,
                [e]: 'blue'})
        }
        if(newUser.password.length < 8) {
            setErrors(["Password must be at least 8 characters"])
        }
        if(errors.length === 0){
            return true
        }
    }

    return (
        <>
            <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger mt-3" key={i}>{err}</p> 
                        })
                    }
                    <button type="submit" className="btn btn-primary">Register</button>
                </form>
            </div>
        </>
    )

}
export default Register
