import { registerUser } from "../services/internalApiService"
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'

const Register = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState([])
    const [currentUser, setCurrentUser] = useState({
        username: "",
        score: 0,
    })
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        const newUser = {
            username: username,
            password: password
        }
        registerUser(newUser)
            .then(data => {
                    console.log(data)
                    localStorage.setItem('token', data.user)
                    navigate('/login')
            })
            .catch(err => {
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr);
            })
    }

    return (
        <>
            <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    <button type="submit" className="btn btn-primary">Register</button>
                </form>
            </div>
        </>
    )

}
export default Register
