import { registerUser } from "../services/internalApiService"
import { useNavigate } from 'react-router-dom'
import { useState } from 'react'

const Register = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState([])
    const [isLoading, setIsLoading] = useState(null)

    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        setIsLoading(true)
        setError(null)
        let newUser = { username: username, password: password }
            registerUser(newUser)
            .then(res=>console.log(res)) // If successful, do something with the response. 
            .catch(err=>{
                const errorResponse = err.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr);
            })   
    }
    return (
        <>
        <h1 className="mt-5 mb-5" style={{fontSize: "100px", color: "white"}}>Register</h1>
            <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    {errors.map((err, index) => <p key={index}>{err}</p>)}

                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>

                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />

                    <button type="submit" className="btn btn-primary mt-3">Register</button>
                </form>
            </div>
        </>
    )

}
export default Register
