import React, { useState, useEffect } from "react"
import Help from "./Help"
import { Link, useNavigate, useHistory } from 'react-router-dom'
import MenuButtonSVG from "./MenuButtonSVG"
import Game from "./Game"
import './styles/Menu.css'
import jwt from 'jwt-decode'


const Menu = () => {
    const [startHelp, setStartHelp] = useState(false)
    const [startGame, setStartGame] = useState(false)
    const navigate = useNavigate()
    const history = useHistor
    useEffect(() => {
        const token = localStorage.getItem('token')
        if(token) {
            const user = jwt.decode(token)
            if(!user){
                localStorage.removeItem('token')
                history.replace('/login')
            } else{

            }
        }
    }, [])
    return (
        <>
        {
            startHelp ? 
            <Help />
            :
            ''
        }
        {
            startGame ?
            <Game />
            :
            <>
            <div className="title">
                <img src={require('../codecrackdownlogo.png')} alt="" className="img-fluid banner-img"></img>
                <div className="circle">
                <div className="fa fa-unlock spinningKey"></div>
                </div>
                <h2 className="info">USERNAME: </h2>
                <h2 className="info">SCORE: </h2>
                <Link id="start" className="menu-btn" to="/game"> 
                    <span>PLAY</span>
                    <MenuButtonSVG />
                </Link>
                <button id="shop" className="menu-btn" onClick={() => setStartGame(true)}>
                    <span>SHOP</span>
                    <MenuButtonSVG />
                </button>
                <button id="help" className="menu-btn" onClick={() => setStartHelp(true)}>
                    <span>HELP</span>
                    <MenuButtonSVG />
                </button>
                {/* {
                    currentUser ?
                    <button id="login" className="menu-btn" onClick={() => navigate('/users/register')}>
                        <span>LOGOUT</span> 
                        <MenuButtonSVG />
                    </button>
                    :
                    <button id="login" className="menu-btn" onClick={() => navigate('/users/login')}>
                        <span>LOGIN</span> 
                        <MenuButtonSVG />
                    </button>
                } */}
            </div>
            </>
        }
        </>
    )

}
export default Menu
