import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { loginUser } from "../services/internalApiService"
const Login = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState([])
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()

        const newUser = {
            username: username,
            password: password
        }
        loginUser(newUser)
            .then(data => {
                if(data.user){
                    localStorage.setItem('token', data.user)
                    navigate("/")
                }
            })
            .catch(err => {
                const errRes = err.response.data.errrors
                const errorArr = []
                for(const key of Object.keys(errRes)){
                    errorArr.push(errRes[key].message)
                }
                setErrors(errorArr)
            })
    }

    return (
        <>
                <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label">Username: </label>
                    </div>
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label">Password: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    <button type="submit" className="btn btn-primary">Login</button>
                </form>
            </div>
            </>
    )
}
export default Login
