import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { loginUser } from "../services/internalApiService"
import { validateUser } from "../services/validations"
const Login = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [errors, setErrors] = useState([])
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()
        let returningUser = { username: username, password: password }
            let currentErrors = validateUser(returningUser)
            if(currentErrors.usernameError === "" && currentErrors.passwordError === ""){
            loginUser(returningUser)

            .then(data => {
                console.log(data)
                if(data.user){
                    localStorage.setItem('token', data.user)
                    navigate("/")
                }
            })
            .catch(err => {
                console.log(err)
            })
        }
        setErrors(currentErrors)
    }

    return (
        <>
                <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>
                    {
                        errors.map((err, i) => {
                            return <p className="text-danger" key={i}>{err}</p> 
                        })
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    <button type="submit" className="btn btn-primary">Login</button>
                </form>
            </div>
            </>
    )
}
export default Login
