import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { loginUser } from "../services/internalApiService"
const Login = () => {
    const [username, setUsername] = useState("")
    const [password, setPassword] = useState("")
    const [error, setError] = useState({})
    const navigate = useNavigate()
    const handleSubmit = (e) => {
        e.preventDefault()
        let returningUser = { username, password }
            loginUser(returningUser)

            .then(data => {
                if(data.user){
                    localStorage.setItem('token', data.user)
                    navigate("/")
                }
            })
            .catch(err => {
                const errRes = err.response.data.error.message
                setError(errRes)
            })
    }

    return (
        <>
            <h1 className="mt-5 mb-5" style={{fontSize: "100px", color: "white"}}>Login</h1>
                <div className="container">
                <form action="" onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Username: </label>
                    </div>
                    {
                        error && (
                            <p className="text-danger mt-3">{error.usernameError}</p> 
                        )
                    }
                    <input type="text" className="form-control" onChange={(e) => setUsername(e.target.value)} value={username} />
                    <div className="mb-3">
                        <label htmlFor="input" className="form-label" style={{fontSize: "32px", color: "white"}}>Password: </label>
                    </div>
                    {
                        error.passwordError && (
                            <p className="text-danger mt-3">{error.passwordError}</p> 
                        )
                    }
                    <input type="password" className="form-control" onChange={(e) => setPassword(e.target.value)} value={password} />
                    <button type="submit" className="btn btn-primary mt-3">Login</button>
                </form>
            </div>
            </>
    )
}
export default Login
