export const validateUser = (newUser) => {
    let newErrors = {
        usernameError: "",
        passwordError: "",
    }
    if(newUser.username.length < 8) {
        newErrors.usernameError = "Username must be at least 8 characters"
    } else {
        newErrors.usernameError = ""
    }
    if(newUser.password.length < 8) {
        newErrors.passwordError = "Password must be at least 8 characters"
    } else {
        newErrors.passwordError = ""
    }
    return newErrors
}
